//仅仅反转字母 "a-bC-dEf-ghIj"--->"j-Ih-gfE-dCba"
func reverseOnlyLetters(s string) string {
	bytes := []byte(s)
	//判断是否是字母
	//思路：反转即交换

	var temp byte

	for i, j := 0, len(bytes)-1; i < j; {
		if IsLetter(bytes[i]) {
			//说明需要交换了
			if IsLetter(bytes[j]) {
				//可以交换
				temp = bytes[i]
				bytes[i] = bytes[j]
				bytes[j] = temp
				i++
				j--
			} else {
				j--
			}
		} else {
			i++
		}
	}
	return string(bytes)
}

func IsLetter(b byte) bool {
	if (b >= 'a' && b <= 'z') || (b >= 'A' && b <= 'Z') {
		//说明是字母
		return true
	}
	return false
}

//转置矩阵，不一定是方形矩阵
func transpose(A [][]int) [][]int {

	if len(A) == 0 { //
		return A
	}

	if len(A) == 1 && len(A[0]) == 1 {
		return A
	}

	res := make([][]int, 0)
	for i := 0; i < len(A[0]); i++ {
		col := make([]int, 0)
		for j := 0; j < len(A); j++ {
			col = append(col, A[j][i])
		}
		res = append(res, col)
	}

	return res

}

//数组的度
//到与 nums 拥有相同大小的度的最短连续子数组，返回其长度
func findShortestSubArray(nums []int) int {

	len := len(nums)
	dp := [][]int{}
	//dp[m][n]代表的是从m:n子串的度
	//dp[len][n+1] = dp[len][n] 若nums[n+1]的数量大于了dpdp[m][n],则+1，否则不变
	//dp[len-m-1-1][n] = dp[len-1-m][n] 若nums[len-m]的数量大于了dpdp[m][n],则+1，否则不变
	//dp[0][0] = 1,
	//初始化二维数组切片
	for i := 0; i < len; i++ {
		dp = append(dp, make([]int, len))
	}

	max, min := 0, 50000 //max代表度，min代表最短的为该度的子序列

	//minNums := []int{}

	for m := len - 1; m >= 0; m-- {
		for n := m; n < len; n++ {
			if m == n {
				dp[m][n] = 1
			} else if CountNum(nums[m:n], nums[n]) == dp[m][n-1] {
				dp[m][n] = dp[m][n-1] + 1
			} else {
				dp[m][n] = dp[m][n-1]
			}
			if dp[m][n] > max {
				max = dp[m][n]
				min = n - m + 1
			} else if dp[m][n] == max {
				if min > n-m+1 {
					min = n - m + 1
				}
			}
		}
	}

	//for _,v1 :=range dp{
	//	for _,v2 := range v1{
	//		fmt.Printf("%d\t",v2)
	//	}
	//	fmt.Println()
	//}

	return min
}

func CountNum(nums []int, target int) int {
	res := 0
	for _, v := range nums {
		if v == target {
			res++
		}
	}
	return res
}

//消失的两个数给定一个数组，包含从 1 到 N 所有的整数，但其中缺了两个数字。你能在 O(N) 时间内只用 O(1) 的空间
//没说明数组是有序的
func missingTwo(nums []int) []int {
	//res := make([]int,2)
	var res []int
	i_v := 1
	for i, v := range nums {
		if v-i > i_v+1 { //说明此处缺了两个数
			res = append(res, i+i_v, i+i_v+1)
		} else if v-i > i_v { //说明此处缺了一个数
			res = append(res, i+i_v)
			i_v++
		}
	}
	if len(res) == 0 {
		res = append(res, nums[len(nums)-1]+1, nums[len(nums)-1]+2)
	} else if len(res) == 1 {
		res = append(res, nums[len(nums)-1]+1)
	}

	return res
}
